package pkgs;

public class datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Byte a=23;
		System.out.println("Byte value of a="+a);
		short sh=345;
		System.out.println("short value of sh="+sh);
		
		int i=64;
				System.out.println("int value of i="+i);
		
		float f=5.678f;
		System.out.println("float value of f="+f);
		
		double d=6.987;
		System.out.println("double value of d="+d);
		
		long l=456786554l;
				System.out.println("long value of l="+l);
		
		char c='b';
		System.out.println("character value="+c);
		
		boolean b=30>40;
		System.out.println("boolean b="+b);
		
		String s="abc";	
		System.out.println("String s="+s);
		
		
		
		
		
		
		
		
		
		
		
	}

}
